import React , {Component} from 'react';
import Lightbox from 'react-images';
import { Row , Col } from 'antd';
import { Card } from 'antd';
const { Meta } = Card;

class Content extends Component {
    constructor(props){
        super(props)

        this.state = {
            slideStatus : false,
            slideShow_card_list:[],
            LIGHTBOX_IMAGE_SET:[]

        }
    }

    openSlideShow = ()=>{
        var temp = this.state;
        temp['slideStatus'] =true;
        // var srcList  = this.props.cards.map(item=>{
        //     return item.largeImageURL;
        // })
        srcList = this.props.cards[0].largeImageURL;
        console.log("here",srcList);
        const LIGHTBOX_IMAGE_SET = [
            {
              caption: 'Images',
              srcSet: srcList
              ,
            }
          ];
        temp['LIGHTBOX_IMAGE_SET'] = LIGHTBOX_IMAGE_SET; 
        this.setState(temp);


    }


    render(){
        return(
            this.props.cards?
            <div>
            <div  style={{ background: '#ECECEC', padding: '30px' ,align:'center' }}>    
                 <Row gutter={16}  justify='space-around'> 
                 {this.props.cards?this.props.cards.map(item=>(
                  <Col span={8}>
                  <Card  
                  onClick = {this.openSlideShow}
                  hoverable ={true}
                  style={{ width: 420 , height: 320 , margin:10}}
                  bordered={false}
                  cover={<img alt="example" width='420' height='320'  src={item.largeImageURL}/>} />
                 </Col>
                )
                ):null}
               </Row>

            </div>
            <div>
            <Lightbox
            images={this.state.LIGHTBOX_IMAGE_SET}
            isOpen={this.state.slideStatus}
            onClickNext={this.gotoNextLightboxImage}
        onClose={this.closeLightbox}

          />
          </div>
          </div>
            :null
        );
    }
}

export default Content;
