import React, { Component} from "react";
import { BrowserRouter, Route, Switch } from "react-router-dom";
import ReactDOM from "react-dom";

import ContentComponent from "../container/Content";
import HeaderComponent from "../container/Header";
import FooterComponent from "../container/Footer";


import axios from "axios";
import { Layout,Input } from "antd";
const { Header, Footer, Content, Sider, } = Layout;
const { Search } = Input;


class appRouter extends Component { 
  constructor(props) {
    super(props);
    //console.log("context",this.context);
    this.state = {
      cards: [],
      txt_search: "",
      current_page: "",
      total_cards: ""
    };
  }

  UpdateURLText = val =>{
    console.log(val);
    axios
    .get(
      "https://6jyi4w7nj5.execute-api.us-east-1.amazonaws.com/crontest",
      {
        params: {
          url: val
        }
      },
      { headers: { "Access-Control-Allow-Origin": "*" } }
    )
    .then(res => {
      var temp = this.state;
      temp["shortenUrl"] = res.body;
      this.setState(temp);
      console.log("here",res);
    });

  }


  Callback = val => {
    console.log(val);
    this.setState({ txt_search: val });
    var temp = this.state;
    temp["txt_search"] = val;
    temp["current_page"] = 1;
    this.setState(temp);
    this.updateCards();
    // console.log("here12345",useContext(textSearch));
  };

  Callback_Pagination = (page, pageSize) => {
    console.log("page", page);
    console.log("pageSize", pageSize);
    var temp = this.state;
    temp["current_page"] = page;
    //temp["pageSize"] = pageSize;
    this.setState(temp);
    this.updateCards();
  };

  updateCards() {
    axios
      .get(
        "https://pixabay.com/api/",
        {
          params: {
            key: "12234069-b8c998a7f8cb3cbdb4c3813a7",
            q: this.state.txt_search,
            per_page: 20,
            page: this.state.current_page
          }
        },
        { headers: { "Access-Control-Allow-Origin": "*" } }
      )
      .then(res => {
        var temp = this.state;
        temp["cards"] = res.data.hits;
        temp["total_cards"] = res.data.total;
        this.setState(temp);
        console.log("here",res);
      });
  }

  render() {
    return (

      <Layout>

       <Search
      placeholder="Paste URl here"
      enterButton="SHORTEN URL"
      size="large"
      onSearch={value => console.log(value)}
    />
      <Search
      placeholder="SHORTEN URL"
      enterButton="GOTO URL"
      size="large"
      onSearch={value => this.UpdateURLText(value)}
    />

        <Footer>

        </Footer>
      </Layout>


    );
  }
}

export default appRouter;
